<script>import clerk from "../client/store.js";
export let afterSignInUrl = void 0;
export let afterSignUpUrl = void 0;
export let redirectUrl = void 0;
export let mode = void 0;
function signIn() {
  const opts = { afterSignInUrl, afterSignUpUrl, redirectUrl };
  if (mode === "modal") {
    return $clerk?.openSignIn(opts);
  }
  return $clerk?.redirectToSignIn(opts);
}
</script>

<button type="button" on:click={signIn} {...$$restProps}><slot>Sign In</slot></button>