import { verifyToken } from '@clerk/backend';
export const verifySession = async (secretKey, sessionToken) => {
    if (sessionToken) {
        const issuer = (issuer) => issuer.startsWith('https://clerk.') || issuer.includes('.clerk.accounts');
        const claims = await verifyToken(sessionToken, {
            secretKey,
            issuer,
        });
        return {
            userId: claims.sub,
            claims,
        };
    }
};
